import axios from "axios";

export function getDogs() {
  return async function (dispatch) {
    var json = await axios.get("http://localhost:3001/dogs", {});
    return dispatch({
      type: "GET_DOGS",
      payload: json.data,
    });
  };
}

export function getDogName(name) {
  //name o payload, da igual
  return async function (dispatch) {
    try {
      var json = await axios.get("http://localhost:3001/dogs?name=" + name);
      return dispatch({
        type: "GET_DOG_NAME",
        payload: json.data, //json.data es lo q devuelve esa ruta
      });
    } catch (error) {
      alert("Dog not found ðŸ˜•");
    }
  };
}

// let { name, height, weight, years, origin, image, bred_for, breed_group, life_span, temperament, madeByDB } = req.body;

export function filterDogsByTemperament(payload) {
  return async function (dispatch) {
    try {
      var json = await axios.get(`/dog/?temperament=${payload}`);
      return dispatch({
        type: "GET_DOGS_BY_TEMP",
        payload: json.data,
      });
    } catch (error) {
      console.log(error, "Error on the filters in actions file");
    }
  };
}

