import {
  GET_ALL_DOGS,
  GET_TEMPERAMENTS,
  ORDER_BY_TEMPERAMENT,
} from "../actions";

const initialState = {
  dogs: [],
  allDogs: [],
  temperaments: [],
  detail: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_DOGS:
      return {
        ...state,
        dogs: action.payload,
        allDogs: action.payload,
      };
    case "GET_NAME_DOG":
      return {
        ...state,
        dogs: action.payload,
      };

    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperaments: action.payload,
      };

    case ORDER_BY_TEMPERAMENT:
      const allDogs = state.dogs; //aca tb para el filtro desde todos
      const temperamentFiltered =
        action.payload === "All"
          ? state.allDogs
          : allDogs.filter((el) => {
              return el.temperaments
                ? el.temperaments.includes(action.payload)
                : el.temperaments
                    ?.map((ele) => ele.name)
                    .includes(action.payload);
            });
      return {
        ...state, //me traiego todo lo de estado
        dogs: temperamentFiltered,
      };
    case "POST_DOGS":
      return {
        ...state,
      };

    case "GET_DOG_NAME":
      return {
        ...state,
        dogs: action.payload, //es el arreglo q estoy renderizando
      };
    case "ORDER_BY_NAME": //'Asc. Desc'
      let orderedDogs =
        action.payload === "az"
          ? state.dogs.sort((a, b) => (a.name > b.name ? 1 : -1))
          : state.dogs.sort((a, b) => (a.name < b.name ? 1 : -1));
      return {
        ...state,
        dogs: orderedDogs,
      };

    case "FILTER_CREATED":
      const allDogs2 = state.allDogs;
      let filteredByCreated =
        action.payload === "created"
          ? allDogs2.filter((dog) => dog.createdInDb)
          : allDogs2.filter((dog) => !dog.createdInDb);
      return {
        ...state,
        dogs: action.payload === "All" ? state.allDogs : filteredByCreated,
      };
    case "ORDER_BY_WEIGHT":
      const orderByWeight =
        action.payload === "asc"
          ? state.dogs.sort(
              (a, b) =>
                Number(a.weight.split(" - ")[0]) -
                Number(b.weight.split(" - ")[0])
            )
          : state.dogs.sort(
              (a, b) =>
                Number(b.weight.split(" - ")[0]) -
                Number(a.weight.split(" - ")[0])
            );
      return {
        ...state,
        dogs: orderByWeight,
      };
    case "GET_DETAILS":
      return {
        ...state,
        detail: action.payload,
      };

    default:
      return state;
  }
}

export default rootReducer;
