import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux"; //hooks
import {
  getDogs,
  getTemperament,
  filterDogsByTemperament,
  filterCreated,
  orderByName,
  orderByWeight,
} from "../actions";
import Card from "./Card";

const Home = () => {
  const dispatch = useDispatch();
  const allDogs = useSelector((state) => state.dogs); //me lo trae del reducer
  const allTemperament = useSelector((state) => state.temperament);

  useEffect(() => {
    dispatch(getTemperament());
  }, [dispatch]);

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);
  function handleClick(e) {
    //e es evento
    e.preventDefault();
    dispatch(getDogs()); //me lo resetea
  }

  function handleFilterTemperament(e) {
    e.preventDefault();
    dispatch(filterDogsByTemperament(e.target.value));
    // eslint-disable-next-line no-undef
    setCurrentPage(1);
    // eslint-disable-next-line no-undef
    setOrder(e.target.value);
  }

  return (
    <div className="fondo">
      <Link className="create_button" to="/dog">
        Create Dog
      </Link>
      <button
        className="reload_dogs"
        onClick={(e) => {
          handleClick(e);
        }}
      >
        Reload Dogs
      </button>
      <div>
        <select
          onClick={(e) => {
            handleSort(e);
          }}
        >
          <option value="Asc">A-Z</option>
          <option value="Desc">Z-A</option>
        </select>
        <select onChange={(e) => handleFilterTemperament(e)}>
          <option>Temperaments</option>
          <option value="All">All</option>

          {allTemperament.map((temperament) => (
            <option key={temperament.name} value={temperament.name}>
              {temperament.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default Home;

