import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"; //hooks
import "./Home.css";

const Home = () => {
  const [dogs, setDogs] = useState([]);
  const allDogs = useSelector((state) => state.dogs);

  const [currentPage, setCurrentPage] = useState(1); //use State xq es un estado local. Es 1 xq siempre voy a arrancar en la 1er pag

  const [dogsPerPage /*setDogsPerPage*/] = useState(8);

  const indexOfLastDog = currentPage * dogsPerPage; // 1 * 8 = 8   mas que un index se refiere a cantidad
  const indexOfFirstDog = indexOfLastDog - dogsPerPage; // 8 - 8 = 0
  const currentDogs = allDogs.slice(indexOfFirstDog, indexOfLastDog);

  useEffect(() => {
    const fetchDogData = async () => {
      try {
        const res = await fetch("https://api.thedogapi.com/v1/breeds");
        const data = await res.json();
        setDogs(data);
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchDogData();
  }, []);

  return (
    <>
      <div className="fondo">
        <Link className="create_button" to="/dog">
          Create Dog
        </Link>
      </div>
      {!dogs ? (
        <h1>Loading...</h1>
      ) : (
        <>
          <section className="card">
            <h1>The Dog App</h1>
            <form className="form" autoComplete="off">
              <input
                type="text"
                name="search"
                id="search"
                placeholder="Search for a dog/breed"
              />
            </form>
            <div className="contenedor">
              <div className="cards">
                {dogs.map((dog) => (
                  <article key={dog.id}>
                    <img className="img" src={dog.image.url} alt={dog.name} />
                    <h3>{dog.name}</h3>
                    <p>Bred For:{dog.bred_for}</p>
                  </article>
                ))}
              </div>
            </div>
          </section>
        </>
      )}
    </>
  );
};

export default Home;

