import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react- redux";
import { getDogs, filterDogsByTemperament } from "../actions";
import { Link } from "react-router-dom";
import Card from "../components/";

export default function Home() {
  const dispatch = useDispatch();

  const allDogs = useSelector((state) => state.dogs); //me lo trae del reducer
  const allTemperament = useSelector((state) => state.temperament);
  const [currentPage, setCurrentPage] = useState(1);
  const [dogsPerPage, setDogsPerPage] = useState(8);
  const indexOfLastDog = currentPage * dogsPerPage; // 1 * 8 = 8   mas que un index se refiere a cantidad
  const indexOfFirstDog = indexOfLastDog - dogsPerPage; // 8 - 8 = 0
  const currentDogs = allDogs.slice(indexOfFirstDog, indexOfLastDog);
  //declaro una constante y le digo con el useSelector traeme a traves de esa contante todo lo que esta en el estado de characters

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  function handleClick(event) {
    //preventDefault para que no se me recargue la pagina
    event.preventDefault();
    dispatch(getDogs());
  }

  function handleFilterTemperament(event) {
    event.preventDefault();
    dispatch(filterDogsByTemperament(event.target.value));
  }

  return (
    <div>
      <Link to="/dogs">Crear personaje</Link>
      <h1>Dogs</h1>
      <button
        onClick={(event) => {
          handleClick(event);
        }}
      >
        Volver a cargar todos los perros
      </button>
      <div>
        <select>
          <option value="Asc">A-Z</option>
          <option value="Desc">Z-A</option>
        </select>
        <select onChange={(e) => handleFilterTemperament(e)}>
          <option>Temperaments</option>
          <option value="All">All</option>
          {allTemperament.map((temperament) => (
            <option key={temperament.name} value={temperament.name}>
              {temperament.name}
            </option>
          ))}
        </select>
        {currentDogs?.map((el) => {
          return (
            <Card
              name={el.name}
              image={el.image}
              temperament={
                el.temperament
                  ? el.temperament
                  : el.temperaments?.map((ele, index) =>
                      el.temperaments.length - 1 === index
                        ? ele.name
                        : ele.name + ", "
                    )
              }
              weight={
                el.weight ? el.weight : el.weightMin + " - " + el.weightMax
              }
            />
          );
        })}
      </div>
    </div>
  );
}

