import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"; //
import { Link } from "react-router-dom";
import "./Home.css";
import {
  getDogs,
  getTemperament,
  filterDogsByTemperament,
  filterCreated,
  orderByName,
  orderByWeight,
} from "../actions";
import Card from "./Card";

const Home = () => {
  const dispatch = useDispatch();
  const [dogs, setDogs] = useState([]);
  // const allDogs = useSelector((state) => state.dogs);
  const allTemperament = useSelector((state) => state.temperament);
  const [currentPage, setCurrentPage] = useState(1); //use State xq es un estado local. Es 1 xq siempre voy a arrancar en la 1er pag

  const [dogsPerPage /*setDogsPerPage*/] = useState(8);

  // const indexOfLastDog = currentPage * dogsPerPage; // 1 * 8 = 8   mas que un index se refiere a cantidad
  // const indexOfFirstDog = indexOfLastDog - dogsPerPage; // 8 - 8 = 0
  // const currentDogs = allDogs.slice(indexOfFirstDog, indexOfLastDog);

  useEffect(() => {
    const fetchDogData = async () => {
      try {
        const res = await fetch("https://api.thedogapi.com/v1/breeds");
        const data = await res.json();
        setDogs(data);
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchDogData();
  }, []);

  function handleClick(e) {
    //e es evento
    e.preventDefault();
    dispatch(getDogs()); //me lo resetea
  }

  function handleFilterTemperament(e) {
    e.preventDefault();
    dispatch(filterDogsByTemperament(e.target.value));
    setCurrentPage(1);
    // setOrder(e.target.value);
  }

  function handleFilterCreated(e) {
    e.preventDefault();
    dispatch(filterCreated(e.target.value)); //el payload
    setCurrentPage(1);
    // setOrder(e.target.value);
  }

  function handleSort(e) {
    e.preventDefault();
    dispatch(orderByName(e.target.value)); //el payload
    setCurrentPage(1); //seteo la pag actual 1
    // setOrder(e.target.value); //aca se setea el ordenamiento
  }

  return (
    <div className="fondo">
      <Link className="create_button" to="/dog">
        Create Dog
      </Link>
      <button
        className="reload_dogs"
        onClick={(e) => {
          handleClick(e);
        }}
      >
        Reload Dogs
      </button>
      <div>
        <select
          onClick={(e) => {
            handleSort(e);
          }}
        >
          <option value="Asc">A-Z</option>
          <option value="Desc">Z-A</option>
        </select>
        <select onChange={(e) => handleFilterTemperament(e)}>
          <option>Temperaments</option>
          <option value="All">All</option>

          {allTemperament.map((temperament) => (
            <option key={temperament.name} value={temperament.name}>
              {temperament.name}
            </option>
          ))}
        </select>

        <ul className="card_grid">
          {currentDogs?.map((el) => {
            return (
              <Card
                id={el.id}
                name={el.name}
                image={el.image}
                temperament={
                  el.temperament
                    ? el.temperament
                    : el.temperaments?.map((ele, index) =>
                        el.temperaments.length - 1 === index
                          ? ele.name
                          : ele.name + ", "
                      )
                }
                weight={
                  el.weight ? el.weight : el.weightMin + " - " + el.weightMax
                }
                key={el.id}
              />
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default Home;

//opcion siempre vamos a necesitar que este un value

