import React, { useState, useEffect } from "react";
import "./Home.css";
const Home = () => {
  const [dogs, setDogs] = useState([]);

  useEffect(() => {
    const fetchDogData = async () => {
      try {
        const res = await fetch("https://api.thedogapi.com/v1/breeds");
        const data = await res.json();
        setDogs(data);
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchDogData();
  }, []);

  return (
    <>
      {!dogs ? (
        <h1>Loading...</h1>
      ) : (
        <>
          <section className="card">
            <h1>The Dog App</h1>
            <form className="form" autoComplete="off">
              <input
                type="text"
                name="search"
                id="search"
                placeholder="Search for a dog/breed"
              />
            </form>

            <div>
              {dogs.map((dog) => (
                <article key={dog.id}>
                  <img className="img" src={dog.image.url} alt={dog.name} />
                  <h3>{dog.name}</h3>
                  <p>Bred For:{dog.bred_for}</p>
                </article>
              ))}
            </div>
          </section>
        </>
      )}
    </>
  );
};

export default Home;

