const initialState = {
  dogs: [],
  allDogs: [], //declaro un estado q siempre va a tener todos los perros
  temperament: [],
  detail: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "GET_DOGS":
      return {
        ...state, //guardo el estado
        dogs: action.payload, //en mi estado dogs, q en un ppio es un estado vacío, mandà todo lo q te mande la acción de dogs
        //acá tb me guarda todos los perros para q pueda usarlo cdo filtro y q me los cargue a todos de nuevo, y no sobre el filtro del filtro
      };

    case "GET_DOG_NAME":
      return {
        ...state,
        dogs: action.payload, //es el arreglo q estoy renderizando
      };

    case "GET_TEMPERAMENT":
      return {
        ...state,
        temperament: action.payload,
      };

    case "POST_DOG":
      return {
        ...state,
      };

    case "FILTER_BY_TEMPERAMENT":
      const allBreeds = state.dogs; //aca tb para el filtro desde todos
      const temperamentFiltered =
        action.payload === "All"
          ? state.allDogs
          : allBreeds.filter((el) => {
              return el.temperament
                ? el.temperament.includes(action.payload)
                : el.temperaments
                    ?.map((ele) => ele.name)
                    .includes(action.payload);
            });
      return {
        ...state, //me traiego todo lo de estado
        dogs: temperamentFiltered,
      };
  }
}

export default rootReducer;
