const initialState = {
  dogs: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "GET_DOGS":
      return {
        ...state,
        //estoy en el reducer y le paso el payload de la action
        dogs: action.payload,
      };

    default:
      break;
  }
}

export default rootReducer;
