import {
  GET_ALL_DOGS,
  GET_TEMPERAMENTS,
  ORDER_BY_TEMPERAMENT,
} from "../actions";

const initialState = {
  dogs: [],
  allDogs: [],
  temperaments: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_DOGS:
      return {
        ...state,
        dogs: action.payload,
        allDogs: action.payload,
      };
    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperaments: action.payload,
      };

    case ORDER_BY_TEMPERAMENT:
      let filteredDogs = state.allDogs;
      if (action.payload !== "All") {
        console.log(action.payload, " para diferenciar del otro log");
        filteredDogs = state.allDogs.filter((dog) =>
          dog.temperaments?.includes(action.payload)
        );
      }
      return {
        ...state,
        dogs: filteredDogs,
      };
    case "GET_DOG_NAME":
      return {
        ...state,
        dogs: action.payload, //es el arreglo q estoy renderizando
      };
    case "ORDER_BY_NAME": //'Asc. Desc'
      let orderedDogs =
        action.payload === "az"
          ? state.dogs.sort((a, b) => (a.name > b.name ? 1 : -1))
          : state.dogs.sort((a, b) => (a.name < b.name ? 1 : -1));
      return {
        ...state,
        dogs: orderedDogs,
      };

    case "FILTER_CREATED":
      const createdFilter =
        action.payload === "created"
          ? state.allDogs.filter((el) => el.createdInDb)
          : state.allDogs.filter((el) => !el.createdInDb);
      return {
        ...state,
        dogs: action.payload === "All" ? state.allDogs : createdFilter,
      };
    default:
      return state;
  }
}

export default rootReducer;

