import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"; //hooks
import Card from "./Card";
import { getDogs } from "../actions";
import { Link } from "react-router-dom";
import { filterDogsByTemperament, orderByName } from "../actions";

export default function Home() {
  //todo lo de abajo sería como hacer un mapStateToProps
  const dispatch = useDispatch();

  const allDogs = useSelector((state) => state.dogs);

  // const temperaments = useSelector((state) => state.temperaments).sort(
  //   function (a, b) {
  //     if (a < b) return -1;
  //     else return 1;
  //   }
  // );

  // const [pagina, setPagina] = useState(1);
  // const [porPagina, setPorPagina] = useState(9);
  // const [dogState, setDogsState] = useState("");
  // const [sort, setSort] = useState("");

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  function handleClick(e) {
    e.preventDefault();
    dispatch(getDogs());
  }

  function handleFilteredByTemp(e) {
    e.preventDefault();
    dispatch(filterDogsByTemperament(e.target.value));
  }

  function handleSort(e) {
    e.preventDefault();
    dispatch(orderByName(e.target.value));
    // setPagina(1);
    // setSort(`Ordenado ${e.target.value}`);
  }

  return (
    <div>
      <Link to="/dog"> Crear Dog </Link>
      <h1>DOGS</h1>
      <button
        onClick={(e) => {
          handleClick(e);
        }}
      >
        Volver a cargar Dogs
      </button>
      <div>
        <select onChange={(e) => handleSort(e)}>
          <option value="Asc">A-Z</option>
          <option value="Desc">Z-A</option>
        </select>
        {/* <select onChange={(e) => handleFilteredByTemp(e)}>
          <option value="all">All Temperaments</option>
          {temperaments.map((temp) => {
            return (
              <option value={temp} key={temp}>
                {temp}
              </option>
            );
          })}
        </select> */}
        {allDogs &&
          allDogs.map((el) => {
            <Card
              image={el.image}
              name={el.name}
              temperaments={el.temperaments}
              weight={el.weight}
            />;
          })}
      </div>
    </div>
  );
}

//opcion siempre vamos a necesitar que este un value

//me permite acceder y preguntar despues
//dentro de las opciones tengo value si el value es ascendente entonces hacer esto lo mismo para el desendente
