import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"; //
import { Link } from "react-router-dom";
import "./Home.css";
import {
  getDogs,
  // getTemperament,
  filterDogsByTemperament,
  // filterCreated,
  // orderByName,
  // orderByWeight,
} from "../actions";
import Card from "./Card";

const Home = () => {
  const dispatch = useDispatch();
  const allDogs = useSelector((state) => state.dogs);
  const [dogs, setDogs] = useState([]);
  // const allDogs = useSelector((state) => state.dogs);
  const allTemperament = useSelector((state) => state.temperament);
  // const [currentPage, setCurrentPage] = useState(1); //use State xq es un estado local. Es 1 xq siempre voy a arrancar en la 1er pag

  // const [dogsPerPage /*setDogsPerPage*/] = useState(8);

  useEffect(() => {
    const fetchDogData = async () => {
      try {
        const res = await fetch("https://api.thedogapi.com/v1/breeds");
        const data = await res.json();
        setDogs(data);
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchDogData();
  }, []);

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  function handleClick(e) {
    //e es evento
    e.preventDefault();
    dispatch(getDogs()); //me lo resetea
  }

  function handleFilterTemperament(e) {
    e.preventDefault();
    dispatch(filterDogsByTemperament(e.target.value));
    // setCurrentPage(1);
    // setOrder(e.target.value);
  }

  // function handleFilterCreated(e) {
  //   e.preventDefault();
  //   dispatch(filterCreated(e.target.value)); //el payload
  //   setCurrentPage(1);
  //   // setOrder(e.target.value);
  // }

  return (
    <div className="fondo">
      <Link className="create_button" to="/dog">
        Create Dog
      </Link>
      <button
        onClick={(e) => {
          handleClick(e);
        }}
      >
        Volver a Cargar todos los Dogs
      </button>

      <select>
        <option value="Asc">A-Z</option>
        <option value="Desc">Z-A</option>
      </select>
      <select onChange={(e) => handleFilterTemperament(e)}>
        <option>Temperaments</option>
        <option value="All">All</option>

        {allTemperament.map((temperament) => (
          <option key={temperament.name} value={temperament.name}>
            {temperament.name}
          </option>
        ))}
      </select>
      <select>
        <opcion>Todos</opcion>
        <opcion>Creados</opcion>
        <opcion>De la Api</opcion>
      </select>

      {allDogs?.map((el) => {
        return (
          <Card
            image
            src={el.image.url}
            alt={el.name}
            temperaments={el.temperaments}
            weight={el.weight}
          />
        );
      })}

      <div className="contenedor"></div>
    </div>
  );
};

export default Home;

