import {
  GET_ALL_DOGS,
  GET_DOG_BY_NAME,
  ORDER_BY_NAME,
  GET_TEMPERAMENTS,
  ORDER_BY_TEMPERAMENT,
  FILTER_BY_CREATED,
  GET_DOG_BY_ID,
  CLEAR_DETAILS,
  POST_DOG,
  ORDER_BY_WEIGHT,
  ERROR_IN_NAME,
  CLEAR_ERROR,
  ERROR_IN_POST,
  DELETE_DOG,
} from "../actions";

const initialState = {
  dogs: [],
  allDogs: [],
  temperaments: [],
  dogDetail: {},
  error: null,
  postError: null,
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_DOGS:
      return {
        ...state,
        dogs: action.payload,
        allDogs: action.payload,
      };
    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperament: action.payload,
      };

    case "FILTER_BY_TEMPERAMENT":
      const allDogs = state.dogs; //aca tb para el filtro desde todos
      const temperamentFiltered =
        action.payload === "All"
          ? state.allDogs
          : allDogs.filter((el) => {
              return el.temperament
                ? el.temperament.includes(action.payload)
                : el.temperaments
                    ?.map((ele) => ele.name)
                    .includes(action.payload);
            });
      return {
        ...state, //me traiego todo lo de estado
        dogs: temperamentFiltered,
      };

    default:
      return state;
  }
}

export default rootReducer;

