import {
  GET_ALL_DOGS,
  GET_DOG_BY_NAME,
  ORDER_BY_NAME,
  GET_TEMPERAMENTS,
  ORDER_BY_TEMPERAMENT,
  FILTER_BY_CREATED,
  GET_DOG_BY_ID,
  CLEAR_DETAILS,
  POST_DOG,
  ORDER_BY_WEIGHT,
  ERROR_IN_NAME,
  CLEAR_ERROR,
  ERROR_IN_POST,
  DELETE_DOG,
} from "../actions";

const initialState = {
  dogs: [],
  allDogs: [],
  temperaments: [],
  dogDetail: {},
  error: null,
  postError: null,
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_DOGS:
      return {
        ...state,
        dogs: action.payload,
        allDogs: action.payload,
      };
    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperaments: action.payload,
      };
    case ORDER_BY_TEMPERAMENT:
      const allDogs = state.allDogs;
      let isFound = null;
      const filteredByTemperament =
        action.payload === "All"
          ? allDogs
          : // eslint-disable-next-line array-callback-return
            allDogs.filter((dog) => {
              if (dog.hasOwnProperty("temperament")) {
                return dog.temperament?.includes(action.payload.trim());
              }
              if (dog.hasOwnProperty("temperaments")) {
                isFound = dog.temperaments?.some((element) => {
                  if (element.name === action.payload) {
                    return true;
                  }
                  return false;
                });
                if (isFound) {
                  return dog;
                }
              }
            });
      return {
        ...state,
        dogs: filteredByTemperament,
      };

    default:
      return state;
  }
}

export default rootReducer;
