import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"; //
import { Link } from "react-router-dom";
import "./Home.css";
import { getAllDogs, getTemperaments, orderByTemperament } from "../actions";
import Card from "./Card";
import Paginado from "./Paginado";

const Home = () => {
  const dispatch = useDispatch();
  // const allDogs = useSelector((state) => state.dogs);
  const [dogs, setDogs] = useState([]);
  //me trae del reducer el estado dogs
  const allDogs = useSelector((state) => state.dogs);
  const allTemperament = useSelector((state) => state.temperament);

  const [, setOrder] = useState("");
  const Temperaments = useSelector((state) => state.temperaments);
  //paginado
  const [currentPage, setCurrentPage] = useState(1); //lo seteamos en 1

  //vamos hacer otro estado local me va a indicar cuantos personajes tengo por pagina
  const [dogsPerPage, setDogsPage] = useState(8);

  //me declaro una constante que se va a llamar indice del ultimo personaje
  const indexOfLastDog = currentPage * dogsPerPage; //vale 8*1=8 posicion array
  //va ser igual a la pagina actual donde estoy * por la cantidad de personaje por pagina

  //indice del primer personaje
  const indexOfFirstDog = indexOfLastDog - dogsPerPage; //0

  const currentDogs = dogs.slice(indexOfFirstDog, indexOfLastDog); //le decimos al slice agarra el indice del 1er dog y el indice del ultimo dog
  //los personajes que estan en la pag actual y que me guarde los personaje de cada pag

  const paginado = (pageNumber) => {
    //esta constante paginado es la que me a ayudar al renderizado
    setCurrentPage(pageNumber);
  };

  useEffect(() => {
    dispatch(getAllDogs());
    dispatch(getTemperaments());
  }, [dispatch]);

  function handleClick(e) {
    //e es evento
    e.preventDefault();
    dispatch(getAllDogs()); //me lo resetea
  }

  function handleFilterByTemperament(e) {
    e.preventDefault();
    dispatch(orderByTemperament(e.target.value));
    setCurrentPage(1);
  }

  return (
    <div className="fondo">
      <Link className="create_button" to="/dog">
        Create Dog
      </Link>

      <Paginado
        dogsPerPage={dogsPerPage}
        dogs={dogs.length}
        paginado={paginado}
      />
      <div className="Filters-container">
        <button
          className="btn-refresh"
          onClick={() => window.location.reload()}
        >
          Refresh
        </button>
        <div className="AZZA-Filter">
          <label>Filter by name: </label>
          <select>
            <option value="Select" disabled>
              Select
            </option>
            <option value="az">A-Z</option>
            <option value="za">Z-A</option>
          </select>
        </div>
        <div className="Created-Filter">
          <label>Filter by created: </label>
          <select
            onChange={(e) => {
              //handleFilterByCreated(e);
            }}
          >
            <option value="All">All dogs</option>
            <option value="created">Created in database</option>
            <option value="api">Existent (from API)</option>
          </select>
        </div>
        <div className="Temperaments-Filter">
          <label>Filter by temperament: </label>
          <select
            onChange={(e) => {
              handleFilterByTemperament(e);
            }}
          >
            <option>Temperaments</option>
            <option value="All">All temperaments</option>
            {Temperaments?.map((temperament) => {
              return (
                <option key={temperament.id} value={temperament.name}>
                  {temperament.name}
                </option>
              );
            })}
          </select>
        </div>
        <div className="Weight-Filter">
          <label>Filter by weight: </label>
          <select>
            <option value="Select" disabled>
              Select
            </option>
            <option value="asc">Light</option>
            <option value="desc">Heavy</option>
          </select>
        </div>
      </div>

      <div className="cards">
        {allDogs?.map((el) => {
          return (
            <Card
              key={el.id}
              id={el.id}
              name={el.name}
              image={el.image.url}
              temperament={el.temperament}
              weight={el.weight.metric}
              height={el.height}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Home;

