import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getDetails } from "../actions";
import axios from "axios";

export default function Details() {
  const { id } = useParams();
  const [detail, setDetail] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:3001/dogs/${id}`)
      .then((response) => setDetail(response.data))
      .catch((error) => console.log(error));
  }, [id]);

  return (
    <div className="fondo_3">
      <div className="button_container_detail">
        <button className="button_detail" onClick={() => "/home"}>
          HOME
        </button>

        {detail.length > 0 ? (
          <div>
            <h1>{detail[0].name}</h1>
            <img
              className="image_detail"
              src={detail[0].image}
              alt="not found"
            />
            <p>
              Height :{" "}
              {!detail[0].createdInDb
                ? detail[0].height
                : detail[0].heightMin + " - " + detail[0].heightMax}
            </p>
            <p>
              Weight :{" "}
              {!detail[0].createdInDb
                ? detail[0].weight
                : detail[0].weightMin + " - " + detail[0].weightMax}
            </p>
            <p>Life Span : {detail[0].life_span}</p>
            <p>
              Temperaments :{" "}
              {!detail[0].createdInDb
                ? detail[0].temperament + " "
                : detail[0].temperaments.map((el, index) =>
                    detail[0].temperaments.length - 1 === index
                      ? el.name
                      : el.name + ", "
                  )}
            </p>
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
}
//si mi personaje en 0 no tiene  la propiedad creada en la base d edatos osea le digo mi personaje es de la api entonces traeme mi dog.temperaments
