import React, { useState, useEffect } from "react";
//import Bark from '../../assets/sounds/bark_intro.mp3'
import Card from "./Card";
import Paginado from "./Paginado";
// import NavBar from "../NavBar/NavBar";
// import SearchBar from "../SearchBar/SearchBar";
// import Loader from "../Loader/Loader";
import { useDispatch, useSelector } from "react-redux";
import {
  getAllDogs,
  getTemperaments,
  orderByName,
  orderByTemperament,
  filterDogsByCreated,
  orderByWeight,
} from "../actions";
import { Link } from "react-router-dom";

import "./Home.css";

function Home() {
  //const BARK = new Audio(Bark);
  const DISPATCH = useDispatch();
  const DOGS = useSelector((state) => state.dogs);
  const TEMPERAMENTS = useSelector((state) => state.temperaments);
  const [currentPage, setCurrentPage] = useState(1);
  const [dogsPerPage] = useState(8);
  const [, setOrder] = useState("");
  const [loading, setLoading] = useState(true);
  const LAST_DOG = currentPage * dogsPerPage;
  const FIRST_DOG = LAST_DOG - dogsPerPage;
  const RENDERED_DOGS = DOGS.slice(FIRST_DOG, LAST_DOG);

  useEffect(() => {
    //BARK.play();
    //BARK.loop = false;
    DISPATCH(getAllDogs());
    DISPATCH(getTemperaments());
  }, [DISPATCH]);
  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 1500);
  }, []);

  const paginate = (number) => {
    setCurrentPage(number);
  };
  const handleOrderByName = (e) => {
    e.preventDefault();
    DISPATCH(orderByName(e.target.value));

    setOrder(`Order: ${e.target.value}`);
  };
  function handleFilterByTemperament(e) {
    e.preventDefault();
    DISPATCH(orderByTemperament(e.target.value));
    setCurrentPage(1);
  }
  function handleFilterByCreated(e) {
    DISPATCH(filterDogsByCreated(e.target.value));
    setCurrentPage(1);
  }
  function handleFilterByWeight(e) {
    e.preventDefault();
    DISPATCH(orderByWeight(e.target.value));
    setOrder(`Weight: ${e.target.value}`);
  }
  return (
    <>
      <div className="Pagination">
        <Pagination
          dogsPerPage={dogsPerPage}
          allDogs={DOGS.length}
          currentPage={currentPage}
          paginate={paginate}
        />
      </div>
      <div className="Filters-container">
        <button
          className="btn-refresh"
          onClick={() => window.location.reload()}
        >
          Refresh
        </button>
        <div className="AZZA-Filter">
          <label>Filter by name: </label>
          <select
            defaultValue={"Select"}
            onChange={(e) => {
              handleOrderByName(e);
            }}
          >
            <option value="Select" disabled>
              Select
            </option>
            <option value="az">A-Z</option>
            <option value="za">Z-A</option>
          </select>
        </div>
        <div className="Created-Filter">
          <label>Filter by created: </label>
          <select
            onChange={(e) => {
              handleFilterByCreated(e);
            }}
          >
            <option value="All">All dogs</option>
            <option value="created">Created in database</option>
            <option value="api">Existent (from API)</option>
          </select>
        </div>
        <div className="Temperaments-Filter">
          <label>Filter by temperament: </label>
          <select
            onChange={(e) => {
              handleFilterByTemperament(e);
            }}
          >
            <option value="All">All temperaments</option>
            {TEMPERAMENTS.map((temperament) => {
              return (
                <option key={temperament.id} value={temperament.name}>
                  {temperament.name}
                </option>
              );
            })}
          </select>
        </div>
        <div className="Weight-Filter">
          <label>Filter by weight: </label>
          <select
            defaultValue={"Select"}
            onChange={(e) => {
              handleFilterByWeight(e);
            }}
          >
            <option value="Select" disabled>
              Select
            </option>
            <option value="asc">Light</option>
            <option value="desc">Heavy</option>
          </select>
        </div>
      </div>
      <div className="Cards-Container">
        {RENDERED_DOGS?.map((dog) => {
          return (
            <Link to={`/dog/${dog.id}`} key={dog.id}>
              <Card
                name={dog.name}
                image={
                  dog.image === null
                    ? DogPic
                    : dog.image.url
                    ? dog.image.url
                    : dog.image
                    ? dog.image
                    : DogPic
                }
                temperament={
                  dog.temperament
                    ? dog.temperament.split(", ")[0]
                    : dog.temperaments?.length > 0
                    ? dog.temperaments[0]?.name
                    : "No record"
                }
                weight={dog.weight ? dog.weight : "No record"}
              />
            </Link>
          );
        })}
      </div>
    </>
  );
}

export default Home;
