import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postDog, getTemperaments } from "../actions";
import { useDispatch, useSelector } from "react-redux";

const DogCreate = () => {
  const dispatch = useDispatch();
  const temperaments = useSelector((state) => state.temperaments);

  const [input, setInput] = useState({
    name: "",
    heigth: "",
    weigth: "",
    life_span: "",
    temperaments: [],
  });

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
  }

  useEffect(() => {
    dispatch(getTemperaments());
  }, []);

  return (
    <div>
      <Link as={Link} to="/home">
        <button>HOME</button>
      </Link>
      <h1>BE CREATIVE 🐾</h1>
      <form>
        <div>
          <label>Name:</label>
          <input
            placeholder="Dog Name"
            type="text"
            value={input.name}
            name="name"
          ></input>
        </div>
        <div>
          <label>imagen:</label>
          <input
            placeholder="Image"
            type="img"
            value={input.image}
            name="image"
            alt="not found"
          ></input>
        </div>
        <div>
          <label>Heigth Min:</label>
          <input
            placeholder="Min height"
            type="number"
            value={input.heigthMin}
            name="heigthMin"
          ></input>
        </div>
        <div>
          <label>Heigth Max:</label>
          <input
            placeholder="Max height"
            type="number"
            value={input.heigthMax}
            name="heightMax"
          ></input>
        </div>
        <div>
          <label>Weight Min:</label>
          <input
            placeholder="height Min"
            type="number"
            value={input.weigthMin}
            name="weigthMin"
          ></input>
        </div>
        <div>
          <label>Weight Max:</label>
          <input
            placeholder="weight Max"
            type="number"
            value={input.weigthMax}
            name="weigthMax"
          ></input>
        </div>
        <div>
          <label>Min life years:</label>
          <input
            className="input"
            placeholder="Min life years"
            type="number"
            value={input.lifemin}
            name="lifemin"
          />
        </div>
        <div>
          <label>Max life years:</label>
          <input
            className="input"
            placeholder="Max life years"
            type="number"
            value={input.lifemax}
            name="lifemax"
          />
        </div>
        <select>
          {temperaments.map((temp) => (
            <option value={temp.name}>{temp.name}</option>
          ))}
        </select>

        <button type="submit">Crear Dogs</button>
      </form>
    </div>
  );
};

export default DogCreate;
