import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux"; //hooks
import {
  getDogs,
  getTemperament,
  filterDogsByTemperament,
  filterCreated,
  orderByName,
  orderByWeight,
} from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";

import SearchBar from "./SearchBar";

export default function Home() {
  //todo lo de abajo sería como hacer un mapStateToProps
  const dispatch = useDispatch();
  const allDogs = useSelector((state) => state.dogs); //me lo trae del reducer
  const allTemperament = useSelector((state) => state.temperament);
  //Paginado: un estado con la pag actual y un estado que me setee la pag actual
  const [currentPage, setCurrentPage] = useState(1); //use State xq es un estado local. Es 1 xq siempre voy a arrancar en la 1er pag
  const [dogsPerPage /*setDogsPerPage*/] = useState(8);
  const [, /*order*/ setOrder] = useState("");

  const indexOfLastDog = currentPage * dogsPerPage; // 1 * 8 = 8   mas que un index se refiere a cantidad
  const indexOfFirstDog = indexOfLastDog - dogsPerPage; // 8 - 8 = 0
  const currentDogs = allDogs.slice(indexOfFirstDog, indexOfLastDog);
  // pag 1 -----------0--------------8
  // pag 2 -----------9--------------17

  //traemos del estado los dogs cdo el componente se monta

  useEffect(() => {
    dispatch(getTemperament());
  }, [dispatch]);

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  function handleClick(e) {
    //e es evento
    e.preventDefault();
    dispatch(getDogs()); //me lo resetea
  }

  function handleFilterTemperament(e) {
    e.preventDefault();
    dispatch(filterDogsByTemperament(e.target.value));
    setCurrentPage(1);
    setOrder(e.target.value);
  }

  function handleFilterCreated(e) {
    e.preventDefault();
    dispatch(filterCreated(e.target.value)); //el payload
    setCurrentPage(1);
    setOrder(e.target.value);
  }

  function handleSort(e) {
    e.preventDefault();
    dispatch(orderByName(e.target.value)); //el payload
    setCurrentPage(1); //seteo la pag actual 1
    setOrder(e.target.value); //aca se setea el ordenamiento
  }

  function handleWeight(e) {
    e.preventDefault();
    dispatch(orderByWeight(e.target.value));
    setCurrentPage(1);
    setOrder(e.target.value);
  }

  return (
    <div className="fondo">
      <Link className="create_button" to="/dog">
        Create Dog
      </Link>
      <button
        className="reload_dogs"
        onClick={(e) => {
          handleClick(e);
        }}
      >
        Reload Dogs
      </button>
      <div>
        <select
          onClick={(e) => {
            handleSort(e);
          }}
        >
          <option value="Asc">A-Z</option>
          <option value="Desc">Z-A</option>
        </select>
        <select onChange={(e) => handleFilterTemperament(e)}>
          <option>Temperaments</option>
          <option value="All">All</option>

          {allTemperament.map((temperament) => (
            <option key={temperament.name} value={temperament.name}>
              {temperament.name}
            </option>
          ))}
        </select>

        <select onChange={(e) => handleWeight(e)}>
          <option>Weight</option>
          <option value="Heavy">Heavy</option>
          <option value="Light">Light</option>
        </select>
        <select onChange={(e) => handleFilterCreated(e)}>
          <option>Dogs</option>
          <option value="All">All</option>
          <option value="Created">Created</option>
          <option value="Api">Existent</option>
        </select>

        <SearchBar />

        <ul className="card_grid">
          {currentDogs?.map((el) => {
            return (
              <Card
                name={el.name}
                image={el.image}
                temperament={
                  el.temperament
                    ? el.temperament
                    : el.temperaments?.map((ele, index) =>
                        el.temperaments.length - 1 === index
                          ? ele.name
                          : ele.name + ", "
                      )
                }
                weight={
                  el.weight ? el.weight : el.weightMin + " - " + el.weightMax
                }
                key={el.id}
              />
            );
          })}
        </ul>
      </div>
    </div>
  );
}

//opcion siempre vamos a necesitar que este un value

//me permite acceder y preguntar despues
//dentro de las opciones tengo value si el value es ascendente entonces hacer esto lo mismo para el desendente
