import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { postDog, getTemperaments } from "../actions";
import { useDispatch, useSelector } from "react-redux";

function validate(input) {
  let errors = {};

  if (!input.name.trim()) {
    //input es mi estado local
    errors.name = "Write a name, please";
  } else if (parseInt(input.name)) {
    errors.name =
      "Name is invalid, please use at least one letter at the beginning";
  }

  if (!input.image) {
    errors.image = "Upload an image, please";
  }
  if (!input.temperament) {
    errors.temperament = "Select one or more temperaments, please";
  }
  if (input.heightMin < 0 || input.heightMin > 100) {
    errors.heightMin =
      "Require field, please write a valid number between 1 and 100";
  }
  if (input.heightMax < 1 || input.heightMax > 100) {
    errors.heightMax =
      "Require field, please write a valid number between 1 and 100";
  }
  if (input.heightMax < input.heightMin) {
    errors.heightMin =
      "The minimum value cannot be greater than the maximum value";
  }

  if (input.weightMin < 0 || input.weightMin > 100) {
    errors.weightMin =
      "Require field, please write a valid number between 1 and 100";
  }
  if (input.weightMax < 1 || input.weightMax > 100) {
    errors.weightMax =
      "Require field, please write a valid number between 1 and 100";
  }
  if (input.weightMax < input.weightMin) {
    errors.weightMin =
      "The minimum value cannot be greater than the maximum value";
  }

  if (input.lmin < 0 || input.lmin > 19) {
    errors.lmin = "Require field, please write a valid number between 1 and 19";
  }
  if (input.lmax < 0 || input.lmax > 19) {
    errors.lmax = "Require field, please write a valid number between 1 and 19";
  }
  if (input.lmax < 10 && input.lmax < input.lmin) {
    errors.lmin = "The minimum value cannot be greater than the maximum value";
  }
  return errors;
}

const DogCreate = () => {
  const dispatch = useDispatch();
  const [errors, setErrors] = useState({});
  const navigate = useNavigate(); //se usa para redirigir a alguna ruta //v de react reemplaza a useHistory//me redirige a la ruta que yo le diga
  const temperaments = useSelector((state) => state.temperaments);

  const [input, setInput] = useState({
    image: "https://placedog.net/640/480?random",
    name: "",
    heightMin: "",
    heightMax: " ",
    weightMin: "",
    weightMax: "",
    weight: "",
    life_span: "",
    temperaments: [],
  });

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
    console.log(input);
  }

  function handleSelect(e) {
    setInput({
      ...input,
      temperaments: [...input.temperaments, e.target.value],
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    console.log(input);
    setErrors(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
    if (
      !Object.keys(errors).length &&
      input.name &&
      input.image &&
      input.heightMin &&
      input.heightMax &&
      input.weightMin &&
      input.weightMax &&
      input.lmin &&
      input.lmax &&
      input.temperament
    ) {
      input.heightMax += " cm";
      input.weightMax += " kgs";
      input.life_span = input.lmin + " - " + input.lmax + " years";
      dispatch(postDog(input));
      alert("Dog created 🐶");
      setInput({
        image: "https://placedog.net/640/480?random",
        name: "",
        heightMin: "",
        heightMax: "",
        weightMin: "",
        weightMax: "",
        weight: "",
        life_span: "",
        temperament: [],
      });
    } else {
      alert("ERROR: Dog not created 😕");
      return;
    }
    navigate("/home");
  }

  function handleDelete(el) {
    setInput({
      ...input, //me traigo el anterior para no pisarlo
      temperament: input.temperaments.filter((temp) => temp !== el),
    });
  }

  useEffect(() => {
    dispatch(getTemperaments());
  }, [dispatch]);

  return (
    <div>
      <Link as={Link} to="/home">
        <button>HOME</button>
      </Link>
      <h1>BE CREATIVE 🐾</h1>
      <form onSubmit={(e) => handleSubmit(e)}>
        <div>
          <label>Name:</label>
          <input
            placeholder="Dog Name"
            type="text"
            value={input.name}
            name="name"
            onChange={(e) => handleChange(e)}
          ></input>
        </div>
        <div>
          <label>imagen:</label>
          <input
            placeholder="Image"
            type="img"
            value={input.image}
            name="image"
            onChange={(e) => handleChange(e)}
            alt="not found"
          ></input>
        </div>
        <div>
          <label>Heigth Min:</label>
          <input
            placeholder="Min height"
            type="number"
            value={input.heightMin}
            name="heightMin"
            onChange={(e) => handleChange(e)}
          ></input>
        </div>
        <div>
          <label>Heigth Max:</label>
          <input
            placeholder="Max height"
            type="number"
            value={input.heightMax}
            name="heightMax"
            onChange={(e) => handleChange(e)}
          ></input>
        </div>
        <div>
          <label>Weight Min:</label>
          <input
            placeholder="Min weight"
            type="number"
            value={input.weightMin}
            name="weightMin"
            onChange={(e) => handleChange(e)}
          ></input>
        </div>
        <div>
          <label>weigth Max:</label>
          <input
            placeholder="Max weight"
            type="number"
            value={input.weightMax}
            name="weightMax"
            onChange={(e) => handleChange(e)}
          ></input>
        </div>
        <div>
          <label>life span:</label>
          <input
            placeholder="life span"
            type="number"
            value={input.life_span}
            name="life_span"
            onChange={(e) => handleChange(e)}
          ></input>
        </div>
        {/* <div>
          <label>Max life years:</label>
          <input
            className="input"
            placeholder="Max life years"
            type="number"
            value={input.lifemax}
            name="lifemax"
            onChange={(e) => handleChange(e)}
          />
        </div> */}
        <select onChange={(e) => handleSelect(e)}>
          {temperaments.map((temp) => (
            <option key={temp.id} value={temp.name}>
              {temp.name}
            </option>
          ))}
        </select>
        <ul>
          <li>{input.temperaments.map((el) => el + " ,")}</li>
        </ul>

        <button type="submit">Crear Dogs</button>
      </form>
    </div>
  );
};

export default DogCreate;
