import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postDog, getTemperaments } from "../actions";
import { useDispatch, useSelector } from "react-redux";

const DogCreate = () => {
  const dispatch = useDispatch();
  const temperaments = useSelector((state) => state.temperaments);

  const [input, setInput] = useState({
    name: "",
    heigth: "",
    weigth: "",
    life_span: "",
    temperaments: [],
  });

  useEffect(() => {
    dispatch(getTemperaments());
  }, []);

  return (
    <div>
      <Link as={Link} to="/home">
        <button>HOME</button>
      </Link>
      <h1>BE CREATIVE üêæ</h1>
      <form>
        <div>
          <label>Nombre:</label>
          <input type="text" value={input.name} name="name"></input>
        </div>
        <div>
          <label>image:</label>
          <input
            placeholder="Image"
            type="img"
            value={input.image}
            name="image"
            alt="not found"
          ></input>
        </div>
        <div>
          <label>Heigth:</label>
          <input type="text" value={input.heigth} name="heigth"></input>
        </div>
        <div>
          <label>Weigth:</label>
          <input type="text" value={input.weigth} name="weigth"></input>
        </div>
        <div>
          <label>life_spa:</label>
          <input type="text" value={input.life_span} name="life_span"></input>
        </div>
        <select>
          {temperaments.map((temp) => (
            <option value={temp.name}>{temp.name}</option>
          ))}
        </select>
      </form>
    </div>
  );
};

export default DogCreate;
